ype(&quot;text/html;charset=utf-8&quot;);// 为解析类提供配置信息DiskFileItemFactory factory = new DiskFileItemFactory();// 创建解析类的实例ServletFileUpload suf = new ServletFileUpload(factory);// 设置文件大小suf.setFileSizeMax(1024 * 400);String title = &quot;&quot;;String author = &quot;&quot;;String imgUploadPath = &quot;&quot;;String fileName = &quot;&quot;;try {List&lt;FileItem&gt; items = suf.parseRequest(request);for (FileItem item : items) {// isFormField为true，表示这不是文件上传表单域if (!item.isFormField()) {ServletContext sctx = getServletContext();//返回包含给定虚拟路径的实际路径。String path = sctx.getRealPath(&quot;/upload&quot;);fileName = item.getName();fileName = fileName.substring(fileName.lastIndexOf(&quot;\\&quot;) + 1);imgUploadPath = path + &quot;\\&quot; + fileName;System.out.println(imgUploadPath + &quot;=========&quot;);// &quot;\&quot;会被转义，需要输入&quot;\\\\&quot;用于表示&quot;\&quot;imgUploadPath = imgUploadPath.replaceAll(&quot;\\\\&quot;, &quot;/&quot;);System.out.println(imgUploadPath + &quot;=========&quot;);File file = new File(imgUploadPath);if (!file.exists()) {// 创建文件所需的目录(用于保存上传的图片)file.getParentFile().mkdirs();item.write(file);}} else {if (item.getFieldName().equals(&quot;title&quot;)) {title = item.getString();} else if (item.getFieldName().equals(&quot;author&quot;)) {author = item.getString();}}}//保存图片的相对路径String rootPath = &quot;/upload/&quot; + fileName;String sql = &quot;insert into lab06(title,author,file) &quot; + &quot;values('&quot; + title + &quot;','&quot; + author + &quot;','&quot;+ rootPath + &quot;')&quot;;DBBean.executeUpdate(sql);response.sendRedirect(&quot;lab06_jsp/list.jsp&quot;);} catch (FileUploadException e) {e.printStackTrace();} catch (Exception e) {e.printStackTrace();}}protected void doPost(HttpServletRequest request, HttpServletResponse response)throws ServletException, IOException {doGet(request, response);}} 3. 图片分页显示界面：list.jsp &lt;%@page import=&quot;java.sql.ResultSet&quot;%&gt;&lt;%@page import=&quo